import * as React from "react"
import { NextPage } from "next"
import Layout from "../../components/Layout"
import { useRouter } from "next/router"
import Carousel, { Modal, ModalGateway } from "react-images"
import { LazyImage } from "../../components/lazyImage"

const ProjectPage: NextPage = ({}) => {
    const router = useRouter()
    const { project } = router.query
    const [currentImage, setCurrentImage] = React.useState(0)
    const [viewerIsOpen, setViewerIsOpen] = React.useState(false)

    const openLightbox = React.useCallback(index => {
        setCurrentImage(index)
        setViewerIsOpen(true)
    }, [])

    const closeLightbox = () => {
        setCurrentImage(0)
        setViewerIsOpen(false)
    }

    const photos = [
        {
            source:
                "https://images.unsplash.com/photo-1558981359-219d6364c9c8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
            alt: "man on bike"
        },
        {
            source:
                "https://images.unsplash.com/photo-1558981359-219d6364c9c8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
            alt: "man on bike"
        },
        {
            source:
                "https://images.unsplash.com/photo-1558981359-219d6364c9c8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
            alt: "man on bike"
        },
        {
            source:
                "https://images.unsplash.com/photo-1558981359-219d6364c9c8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
            alt: "man on bike"
        },
        {
            source:
                "https://images.unsplash.com/photo-1558981359-219d6364c9c8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
            alt: "man on bike"
        },
        {
            source:
                "https://images.unsplash.com/photo-1558981359-219d6364c9c8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
            alt: "man on bike"
        }
    ]

    return (
        <Layout>
            <div className="container">
                <h1>{project}</h1>
                <div className="photo-grid">
                    {photos.map((photo, index) => (
                        <LazyImage
                            src={photo.source}
                            alt={photo.alt}
                            key={index}
                            onclick={openLightbox.bind(null, index)}
                        />
                    ))}
                </div>
            </div>
            <ModalGateway>
                {viewerIsOpen ? (
                    <Modal onClose={closeLightbox}>
                        <Carousel
                            currentIndex={currentImage}
                            views={
                                photos &&
                                photos.map(x => ({
                                    ...x
                                }))
                            }
                        />
                    </Modal>
                ) : null}
            </ModalGateway>

            <style jsx>{`
                .container {
                    margin: 200px auto 0 auto;
                    max-width: 1080px;
                }
                .container h1 {
                    text-align: center;
                    font-size: 55px;
                    margin-bottom: 50px;
                }
                .photo-grid {
                    display: grid;
                    gap: 1rem;
                    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
                    grid-auto-rows: 240px;
                }
            `}</style>
        </Layout>
    )
}

export default ProjectPage
