    width: 150px;
    height: 150px;
    background-color: black;
    border-radius: 4px;
    opacity: 0;
`

const IndexPage: NextPage = () => {
    const containerVariants = {
        before: {},
        after: { transition: { staggerChildren: 0.06 } }
    }

    // Variants for animating each letter
    // Set transform origin for desired effect
    const letterVariants = {
        before: {
            rotate: 90,
            opacity: 0,
            transformOrigin: "left bottom",
            transition: {
                ease: "easeOut",
                y: { stiffness: 1000, velocity: -100 }
            }
        },
        after: {
            rotate: 0,
            opacity: 1,
            transformOrigin: "left bottom",
            transition: {
                ease: "easeOut",
                y: { stiffness: 1000, velocity: -100 }
            }
        }
    }
    return (
        <Layout>
            <div className="hero-section">
                <div className="container w-container">
                    <div className="text-side">
                        <p className="body-text">100ndup</p>
                        <MotionDiv />
                        <motion.div>
                            {
                                // Set the display and justifyContent properties to align the text correctly
                            }
                            <motion.div
                                style={{
                                    fontFamily:
                                        "Montserrat, Work Sans, sans-serif",
                                    fontWeight: "bold",
                                    letterSpacing: "-0.04em",
                                    fontSize: 26,
                                    color: "#000",
                                    display: "flex",
                                    justifyContent: "center"
                                }}
                                variants={containerVariants}
                                initial={"before"}
                                animate={"after"}
                            >
                                // Set width to "auto" in order to ensure
                                default kerning
                                <motion.div
                                    custom={1}

                                    variants={letterVariants}
                                    style={{
                                        width: "auto",
                                        position: "relative"
                                    }}
                                >
                                    <strong>
                                        Gain a competitive advantage
                                        <br />
                                        in a connected digital world
                                    </strong>
                                </motion.div>
                            </motion.div>
                        </motion.div>
                        {/* <h1 className="heading1">
                            <strong>
                                Gain a competitive advantage
                                <br />
                                in a connected digital world
                            </strong>
                        </h1> */}
                    </div>
                </div>
            </div>
            <style jsx>
                {`
                    .body-text {
                        color: #2b2c30;
                        font-size: 24px;
                        line-height: 28px;
                        font-weight: 300;
                    }
                    .heading1 {
                        color: #2b2c30;
                        font-size: 47px;
                        line-height: 70px;
                        font-weight: 900;
                    }
                    .hero-section {
                        display: -webkit-box;
